  RTC0DAT = RTC0XCN_XMODE__CRYSTAL | RTC0XCN_AGCEN__DISABLED
      | RTC0XCN_BIASX2__DISABLED | RTC0XCN_LFOEN__DISABLED;
  while ((RTC0ADR & RTC0ADR_BUSY__BMASK) == RTC0ADR_BUSY__SET)
    ;    //Poll Busy Bit

