  // [PMU0FL - Power Management Unit Flag]$

  // $[PMU0MD - Power Management Unit Mode]
  /***********************************************************************
   - Disable the buffered RTC output
   - Enable the wake-up request signal
   ***********************************************************************/
  PMU0MD = PMU0MD_RTCOE__DISABLED | PMU0MD_WAKEOE__ENABLED;
  // [PMU0MD - Power Management Unit Mode]$

  // $[PMU0CF - Power Management Unit Configuration 1]
  /***********************************************************************
   - A RTC Alarm caused the last wake-up
   - An RTC oscillator fail event caused the last wake-up
   ***********************************************************************/
  PMU0CF |= PMU0CF_RTCAWK__SET | PMU0CF_RTCFWK__SET;
  // [PMU0CF - Power Management Unit Configuration 1]$

  // $[PCON - Power Control]
  // [PCON - Power Control]$

  // $[PMU0CF - Power Management Unit Configuration 2]
  /***********************************************************************
   - Place the device in Suspend mode
   ***********************************************************************/
